import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
    name: 'cart-slice',
    initialState: {items: 0},
    reducers: {
        increase(state){
            state.items = items + 1;
        },
        decrease(state){
            state.items = items - 1;
        }
    }
})

export const cartActions = cartSlice.actions;

export default cartSlice;
